import React, { useState, useEffect } from 'react'

const addr = 'https://react-http-1-f6f8f-default-rtdb.firebaseio.com/tasks.json, method = "GET"'

export const useFetch = async (method = 'GET', address = addr,) => {

    const [isLoading, setIsLoading] = useState(false)
    const [error, setError] = useState(null)
    const [data, setData] = useState({})

    const fetchItem = async (method, taskText, address) => {

        setIsLoading(true);
        setError(null);

        try {

            let resp

            if (method === "POST") {
                resp = await fetch(
                    address, {
                    method: 'POST',
                    body: JSON.stringify({ text: taskText }),
                    headers: {
                        'Content-Type': 'application/json',
                    },
                })
            } else {
                resp = await fetch(
                    address)
            }


            if (!resp.ok) {
                throw new Error('Request failed!');
            }

            const data = await resp.json();

            return data

        
        } catch (err) {
            setError(err.message || 'Something went wrong!');
        }
        setIsLoading(false);
    }
    return [ fetchItem, isLoading, error];
}
