import React, { useState, useEffect, useCallback } from 'react'

export const addr = 'https://react-http-1-f6f8f-default-rtdb.firebaseio.com/tasks.json, method = "GET"'

export const useFetch = async (applyData) => {

    const [isLoading, setIsLoading] = useState(false)
    const [error, setError] = useState(null)
    const [ data, setData] = useState({})

    const fetchItem = useCallback(async (requestConfig) => {

        setIsLoading(true);
        setError(null);

        try {

            let resp = await fetch(
                requestConfig.url, {
                    method: requestConfig.method ? requestConfig.method : "GET",
                    headers: requestConfig.headers ? requestConfig.header : {} , 
                    body: requestConfig.headers ? JSON.stringify(requestConfig.body) : null
                }
                )

            if (!resp.ok) {
                throw new Error('Request failed!');
            }

            const data = await resp.json();

            applyData(data)

        
        } catch (err) {
            setError(err.message || 'Something went wrong!');
        }
        setIsLoading(false);
    }, [ applyData])
   
    return {
        isLoading,
        error,
        fetchItem
    }
}
